options:
chart.options = {
  scales: {
    x: {
      type: "time",
      time: {
        unit: "day",
        displayFormats: {
          day: 'LLL dd T'
        }
      }
    }
  },
  parsing: false,
  spanGaps: true,
  responsive: true,
  plugins: {
    legend: {
      position: "bottom",
    },
    title: {
      display: true,
      text: "Schedules planned / actual"
    }
  },
  datasets: {
    line: {
      showLine: false
    }
  }
}
